import React, { useState,useEffect } from 'react';
import './Nav.css';

function Nav() {
    const [show,handleShow]=useState(false);
    const [inputValue, setInputValue] = React.useState("");
    useEffect(() =>{
        window.addEventListener("scroll",()=>{
            if(window.scrollY>100){
                handleShow(true);
            }else handleShow(false);
        });
        return () => {
            window.removeEventListener("scroll",null);
        };
    },[]);
    function searchChangeHandler(event){
        setInputValue(event.target.value);
        // console.log(inputValue);
    }

    function keypress(event){
        if(event.keyCode==13){
            pass_this=event.target.value;            
        }
    }
    return (
        <div className={`nav ${show && "nav__black"}`}>
            <img className="nav__logo"
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/0/08/Netflix_2015_logo.svg/799px-Netflix_2015_logo.svg.png"
            alt="Netflix-logo"/>
   <input type="search" className="search" id="search_mv" value={inputValue} onKeyDown={keypress} onChange={searchChangeHandler} placeholder="Search here.."/>

<Search search_in={pass_this}/>  
            <img className="nav__avatar"
            src="https://t4.ftcdn.net/jpg/03/23/80/43/240_F_323804352_GEjabYYebyrfHyMLtfPPrZeh0wYsZQgf.jpg"
            alt="Netflix-logo"/>


        </div>
    )
}

export default Nav
